        self._list = MeasurementList(device, f"{self._cmd_syntax}:LIST")
        self._lockrj = MeasurementLockrj(device, f"{self._cmd_syntax}:LOCKRJ")
        self._lockrjvalue = MeasurementLockrjvalue(device, f"{self._cmd_syntax}:LOCKRJValue")
