        self._hysteresis = TriggerHysteresis(device, f"{self._cmd_syntax}:HYSTeresis")
        self._state = TriggerState(device, f"{self._cmd_syntax}:STATE")
        self._status = TriggerStatus(device, f"{self._cmd_syntax}:STATUs")
